INSERT INTO djangotest.movie_movieInfo (id, sentiment, start_title, short, score) VALUES (36, 0.00, '力荐', '在看到最后一个镜头前还懵懵懂懂，觉得有很多细节串不起来。打算打三星半。结果那面墙一出来，我艹，屌爆了这编故事的能力和反应。五星推荐！', 5);
