INSERT INTO djangotest.movie_movieInfo (id, sentiment, start_title, short, score) VALUES (40, 0.00, '推荐', '满是算计的好剧本，“I am smarter than you”这句台词简直是影史超级大flag。最棒的镜头，莫过于最后史派西的跛脚变正常那段，太酷了！', 4);